properties.default:
"css`
    :host {
      display: block;
    }

    .content-block {
      border-radius: var(--radius-2);
      display: grid;
      gap: var(--size-3);
      background: hsl(var(--gray-0-hsl));
      padding: var(--size-4);

      transform: scale(0.98);
      transition:
        transform 250ms var(--ease-out-1),
        box-shadow 300ms var(--ease-1);
    }

    .visible {
      transform: scale(1);
      box-shadow: var(--shadow-2);
    }

    .content-block__heading {
      margin: 0;
      font-size: var(--size-4);
      font-family: 'Lilita One', sans-serif;
      font-size: var(--font-size-4);
      font-weight: var(--font-weight-3);
      line-height: var(--font-lineheight-3);
    }
  `"

"css`
    :host {
      display: block;
      /* cursor: none; */
    }

    .cursor {
      position: relative;
      z-index: 1000;
    }
  `"

"css`
    :host {
      display: block;
      pointer-events: none;
    }

    .cursor {
      position: fixed;
      inset: 0;
    }

    circle {
      opacity: 0;
      transform: scale(1.4);
      transition:
        transform 250ms ease-in,
        opacity 250ms ease-in;
    }

    .moving circle {
      opacity: 1;
      transform: scale(1);
    }

    svg {
      width: 100%;
      height: 100%;
      stroke: currentColor;

      transform: rotate(var(--rotation)) translate(-4px, -4px);
    }
  `"

"css`
    :host {
      display: block;
    }

    strong {
      font-family: 'Lilita One', sans-serif;
    }

    p {
      margin: 0;
      font-size: var(--font-size-1);
      line-height: var(--font-line-height-0);
    }

    figure {
      margin-block: var(--size-fluid-2);
      margin-inline: var(--size-fluid-3);
    }

    figure blockquote {
      margin-block: var(--size-fluid-1);
      margin-inline: var(--size-fluid-3);
    }

    .about-content {
      display: grid;
      gap: var(--size-8);
    }

    content-block {
      transition: transform 250ms var(--ease-1);
      transform: translateX(0) rotate(0);
      transform-origin: 100% 100%;
    }

    @media (min-width: 768px) {
      content-block:nth-child(odd) {
        transform-origin: 100% 100%;
        transform: translateX(var(--size-fluid-6)) rotate(1deg);
      }

      content-block:nth-child(even) {
        transform-origin: 0 100%;
        transform: translateX(calc(-1 * var(--size-fluid-5))) rotate(-1deg);
      }
    }
  `"

"css`
    :host {
      display: block;
    }

    .hello {
      opacity: 1;
    }

    img {
      display: block;
      max-width: 100%;
      height: auto;

      grid-area: image;

      opacity: 0;
      transition: opacity 500ms var(--ease-out-5);
    }

    .profile-image {
      max-width: var(--size-12);
      background-image: var(--gradient-14);
      border-radius: var(--radius-blob-5);
      padding: var(--size-2);

      display: grid;
      grid-template-areas: 'image';
    }
  `"

"css`
    :host {
      --gap: var(--size-3);
      --font-family-title: 'Lilita One', sans-serif;
      --font-size-title: var(--font-size-5);
      --font-weight-title: var(--font-weight-3);
      --font-lineheight-title: var(--font-lineheight-1);

      display: block;
    }

    a {
      color: inherit;
    }

    .site-header {
      display: grid;
      gap: var(--gap);
      grid-template-areas: 'title social';
      grid-template-columns: 1fr auto;
    }

    .site-header__title {
      grid-area: title;
      font-family: var(--font-family-title);
      font-size: var(--font-size-title);
      font-weight: var(--font-weight-title);
      line-height: var(--font-lineheight-title);

      margin: 0;
    }

    .site-header__social {
      display: flex;
      align-items: center;
      gap: var(--size-3);
    }
  `"
